import React from 'react'
import Button from './Button'
import { GrClose } from "react-icons/gr";
import { useState } from 'react';

const PopupCard = ({ currentRow, displayCard, onClick }) => {
    const [historyText, setHistoryText] = useState()
    const history = currentRow.probabilityHistory
    if(history == undefined || history.length <= 0){
        console.log('undefined')
    }else{
        let historyItems = history.map(item => 
        <ul>
            <li>{item.daysAgo}</li>
            <li>{item.pilytixProb}</li>
            <li>{item.repProb}</li>
        </ul>)
        setHistoryText(historyItems)
    }

  return (
    <div className={`popupCard ${displayCard ? 'show' : ''}`}>
        <div className="popupCardItems">
            <Button onClick={onClick} classText={'closeCard'} text={<GrClose />}  />
            <p>Id: {currentRow.oppId}</p>
            <h1>Opp Name: {currentRow.oppName}</h1>
            <p>{currentRow.salesRepName}</p>
            <p>{currentRow.amount}</p>
            <p>{currentRow.product}</p>
            <p>{currentRow.stage}</p>
            <p>{currentRow.repProbability}</p>
            <p>{currentRow.pilytixTier}</p>
            <p>{currentRow.pilytixProbability}</p>
            <div>
                {historyText}
            </div>  
        </div>
    </div>
  )
}

export default PopupCard
